import React, { Component, createRef } from 'react';
import Category from './Category';
import api from '../../helpers/api';

class CategoryContainer extends Component {
    state = {
        category: null,
        currentQuestion: 0,
    }
    answerInput = createRef();
    focusAnswerInput = focusAnswerInput.bind(this);

    focusAnswerInput() {
        this.answerInput.current.focus();
    }

    async componentDidMount() {
        const data = await api.getCategoryById(this.props.match.params.id);
        this.setState( {
        category: data,
        });
    }
  enterPressed = (event, answer) => {
    var code = event.keyCode || event.which;
    var {currentQuestion} = this.state;
    if(code === 13) { 
        console.log('entré validé !');
        var playerAnswer = event.target.value;
        var correctAnswer = this.state.category;
        if(playerAnswer === correctAnswer) {
            console.log('okok');
        } else {
            console.log('et non');
            console.log(correctAnswer);
        }
    } 
}
  render() {
      const { category } = this.state;
      const  currentQuestion  = this.state.currentQuestion;
     console.log(this.state);
     if(!category) return <div>is loading</div>
    return (
      <Category
        category={category}
        currentQuestion = {currentQuestion}
        sub={this.enterPressed}
        ref={this.answerInput}
        refFunc={this.focusAnswerInput}
      />
    );
  }
}

export default CategoryContainer;
