function getClues() {
  fetch('http://jservice.io/api/clues', {method: 'get'})
      .then(response => {
        response.json().then(r => {
          this.setState({
              question: r[this.state.curentQuestion].question,
              answer: r[this.state.curentQuestion].answer.toLowerCase()
          })
          console.log(r);
          });
      })
}
function getRandom() {
  fetch('http://jservice.io/api/random', {method: 'get'})
        .then(response => {
          response.json().then(r => {
            this.setState({
                question: r[this.state.curentQuestion].question,
                answer: r[this.state.curentQuestion].answer.toLowerCase()
            })
            console.log(r);
            });
        })
}

function getCatgory() {
