import React, { Component, createRef } from 'react';
import Category from './Category';
import api from '../../helpers/api';

class CategoryContainer extends Component {
    state = {
        category: null,
        currentQuestion: 0,
        score: 0
    }
    answerInput = createRef();

    async componentDidMount() {
        const data = await api.getCategoryById(this.props.match.params.id);
        this.setState( {
            category: data,
        });
    }

    handleSubmit = (e) => {
        // here I prevent de fault bh of submitting form
        e.preventDefault();
        const { category } = this.state;
        const  currentQuestion  = this.state.currentQuestion;
        const answer = this.answerInput.current.value;
        const currentAnswer = category.clues[currentQuestion].answer;
        console.log(answer.toLowerCase());
        if(answer.toLowerCase() === currentAnswer.toLowerCase()) {
            console.log("okok");
            this.setState({
                currentQuestion: this.state.currentQuestion + 1,
                score: this.state.score + 1
            })
        } else {
            this.setState({
                currentQuestion: this.state.currentQuestion + 1
            })
            console.log('non non');
        }
    }

    enterPressed = (e) => {
        var code = e.keyCode || e.which;
        if(code === 13) { 
            const { category } = this.state;
            const  currentQuestion  = this.state.currentQuestion;
            const answer = this.answerInput.current.value;
            const currentAnswer = category.clues[currentQuestion].answer;
            console.log(answer.toLowerCase());
            if(answer.toLowerCase() === currentAnswer.toLowerCase()) {
                console.log("okok");
                this.setState({
                    currentQuestion: this.state.currentQuestion + 1,
                    score: this.state.score + 1
                })
            } else {
                this.setState({
                    currentQuestion: this.state.currentQuestion + 1
                })
                console.log('non non');
            }
        }
    }

  render() {
      const { category } = this.state;
      const  currentQuestion  = this.state.currentQuestion;
     console.log(this.state);
     if(!category) return <div>is loading</div>
    return (
      <Category
        category={category}
        currentQuestionIndex = {currentQuestion}
        handleSubmit={this.handleSubmit}
        answerInput={this.answerInput}
        enterPressed={this.enterPressed}
        score={this.state.score}
      />
    );
  }
}

export default CategoryContainer;
