import React from 'react';
import PropTypes from 'prop-types';

const Category = ({ category, currentQuestion , sub, ref, refFunc }) => (
  <section className={'question-box'}>
    <h1>Category page: {category.title}</h1>
    <span>Question : {category.clues[currentQuestion].question}</span>
    <input onKeyPress={sub} ref={ref}></input>
    <button>Send answer</button>
  </section>
);

Category.propTypes = {
    category: PropTypes.shape({}).isRequired,
    ref: PropTypes.shape({}).isRequired
};

export default Category;
