import React from 'react';
import PropTypes from 'prop-types';

const Category = ({ category, currentQuestion, enterPressed, textInput}) => (
  <section className={'question-box'}>
    <h1>Category page: {category.title}</h1>
    <span>Question : {category.clues[currentQuestion].question}</span>
    <input ref={textInput}></input>
    <button type={'submit'} onClick={enterPressed}>Send answer</button>
  </section>
);

Category.propTypes = {
    category: PropTypes.shape({}).isRequired,
    currentQuestion: PropTypes.number.isRequired,
    handleSubmit: PropTypes.func.isRequired,
    answerInput: PropTypes.shape({
        value: PropTypes.instanceOf(HTMLInputElement)
}),
};

export default Category;
