import React, { Component } from 'react';
import Category from './Category';
import api from '../../helpers/api';

class CategoryContainer extends Component {
  state = {
    category: null,
    currentQuestion: 0,
  }
  async componentDidMount() {
    const data = await api.getCategoryById(this.props.match.params.id);
    this.setState( {
      category: data,
    });
  }
  enterPressed(event, answer) {
    var code = event.keyCode || event.which;
    if(code === 13) { 
        console.log('entré validé !');
        var playerAnswer = event.target.value;
        //var correctAnswer = ;
       // if(playerAnswer.toLowerCase() === )
    } 
}
  render() {
      const { category } = this.state;
      const  currentQuestion  = this.state.currentQuestion;
     console.log(this.state);
     if(!category) return <div>is loading</div>
    return (
      <Category
        category={category}
        currentQuestion = {currentQuestion}
        sub={this.enterPressed(event, category.clues[currentQuestion].answer)}
      />
    );
  }
}

export default CategoryContainer;
